import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";
import { Icon } from '@iconify/react';
import { API_BASE_URL } from '../../config/serverApiConfig';
import OTPverification from "./otpVerification";
import Footer from "../footer/index";

const Login = () => {
  const [data, setData] = useState({ user: "", password: "" });
  const [errors, setErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleChange = ({ currentTarget: input }) => {
    setData({ ...data, [input.name]: input.value });
  };

  const handleTogglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const validateForm = () => {
    const errors = {};
    if (!data.user.trim()) {
      errors.user = "Username is required";
    }
    if (!data.password.trim()) {
      errors.password = "Password is required";
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // Proceed with form submission
      // For now, let's just log the form data
      console.log(data);
    }
  };

  return (
    <>
      <section className="login-sec p-4">
        <div className="container">
          <div className="row sign-in">
            <div className="col-md-6 p-0">
              <img src="/imgs/login-img.png" alt="imgs" srcSet="" className="h-100" />
            </div>

            <div className="col-md-6">
              <div className="padding-x">
                <div className="heading-img py-2 d-flex justify-content-center">
                  <img src="/imgs/logo.png" alt="" srcSet="" />
                </div>
                <hr />

                <div className="nav-align-top">
                  <form onSubmit={handleSubmit}>
                    <div className="tab-content">
                      <div className="tab-pane fade show active" id="navs-top-home" role="tabpanel">
                        <div className="admin-login-page w-100">
                          <div className="card-body">
                            <h4 className="pb-2 text-center">Welcome back</h4>
                            <input
                              id="emailAddress"
                              className={`form-control ${errors.user ? styles.error : ''}`}
                              type="email"
                              name="user"
                              onChange={handleChange}
                              value={data.user}
                              required
                              placeholder="Email Address"
                            />
                            {errors.user && <div className={styles.error_msg}>{errors.user}</div>}
                            <div className="input-group mb-3">
                              <input
                                id="password"
                                className={`form-control ${errors.password ? styles.error : ''}`}
                                type={showPassword ? "text" : "password"}
                                name="password"
                                onChange={handleChange}
                                value={data.password}
                                required
                                placeholder="Password"
                              />
                              <button
                                className="btn btn-outline-secondary"
                                type="button"
                                onClick={handleTogglePasswordVisibility}
                              >
                                <Icon icon={showPassword ? "oi:eye" : "oi:eye"} />
                              </button>
                              {errors.password && <div className={styles.error_msg}>{errors.password}</div>}
                            </div>
                            <div className="form-check form-switch d-flex gap-2">
                              <input className="form-check-input" type="checkbox" role="switch" id="flexSwitchCheckDefault" />
                              <span>Remember Login ID</span>
                            </div>
                            <button type="submit" className="btn btn-primary" id="signin">
                              Sign In
                            </button>
                          </div>
                        </div>
                      </div>
                      <div className="tab-pane fade" id="navs-top-profile" role="tabpanel">
                        Create New Account
                      </div>
                    </div>
                  </form>
                </div>

                <OTPverification />
              </div>
            </div>
          </div>
        </div>
      </section>
      <Footer />
    </>
  );
};

export default Login;
